
public class FlowEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//60점 이상이면 합격이다
		
		int score = 0;
		
		score = 59;
		
		if(score >= 60) {
			System.out.println("축하드립니다!! 합격입니다.");
		}
		
		if(score < 60) {
			System.out.println("불합격입니다.");
		}
		


	}

}
