
public class FlowEx4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int num = 0;
		
		num = 3;
		
		System.out.println("입력하신 값은 " + num);
		
		if(num % 2 == 1) {
			System.out.println("홀입니다.");
		} else {
			System.out.println("짝입니다.");
		}
	}

}
