
public class OperatorEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		논리부정 연산자 - !
		가독성을 높인다 = 클린코드
	
		*/
		boolean flag = false;
		
		System.out.println(flag);
		flag = !flag;
		
		System.out.println(flag);
		
		System.out.println(!flag);
		
		System.out.println(flag);
		
		

	}

}
