
public class OperatorEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/**
		 * 단항 연산자 ++ , --
		 * 증감 연산자
		 * 
		 * println 안에서 ++이 앞에오면 +1을 하고 출력하고 뒤에 ++이 오면 출력하고 +1을 연산한다
		 */
		
		int n = 5;
		n++; // n = n + 1;
		
		System.out.println(n);
		
		n = 5;
		++n; // n = n + 1;
		System.out.println(n);
		
		n--;
		System.out.println(n);
		
		
	}

}
