
public class OperationTest3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 6;
		int n = 151;
		String result1 = "";
		String result2 = "";
		
		result1 = (0<num && num<9) ? num + "은 0보다 크고 9보다 작다 true" : num + "은 0보다 작거나 9보다 크다false";
		result2 = (0>n || n>100) ? n + "은 0보다 작거나 100보다 크다 true" : n + "은 0보다 크거나 100보다 작다 false";
		
		
		
		
		System.out.println(result1);
		
		System.out.println(result2);
		
		
	
				
				
				
			

	}

}
